import { FormGroup,FormControl, Input, InputLabel, styled, Button } from '@mui/material';
import { useState } from 'react';


const Container=styled(FormGroup)
`
width:50%;
margin: 5% auto 0 auto;
& > div{
  margin-top:20px;
}
`;

const defaultValue={
    name:"",
    email:"",
    phone:"",
    username:""
 }

 const [user, setuser] = useState(user);
 

const onClickValue=(e)=>{
    console.log(e.target.name +" ,"+e.target.value);
    setuser({ ...user , [e.target.name]:e.target.value});
}


const AddUser=()=>{
    return (
       <Container>
           <FormControl>
               <InputLabel>Name</InputLabel>
               <Input onClick={(e)=>onClickValue(e)} name="name" />
           </FormControl> 
           <FormControl>
            <InputLabel>Username</InputLabel>
            <Input onClick={(e)=>onClickValue(e)} name="username" />
           </FormControl>      
           <FormControl>
               <InputLabel>Email</InputLabel>
               <Input onClick={(e)=>onClickValue(e)} name="email" />
           </FormControl>   
           <FormControl>
               <InputLabel>Phone Number</InputLabel>
               <Input onClick={(e)=>onClickValue(e)} name="phone" />
           </FormControl>   
           <FormControl>
               <Button variant="contained">Submit</Button>
           </FormControl> 
       </Container>
       
    )
}

export default AddUser;
